import React from "react";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";

import axios from "axios";

import { API_URL } from "../constants";

class NewUserForm extends React.Component {
  state = {
    userid: 0,
    useremail: "",
    userpassword: "",
    savedate: "",
    changepassworddate: "",
    userroleid: ""
  };

  componentDidMount() {
    if (this.props.user) {
      const { userid, useremail, userpassword, savedate, changepassworddate, userroleid } = this.props.user;
      this.setState({ userid, useremail, userpassword, savedate, changepassworddate, userroleid });
    }
  }

  onChange = e => {
    this.setState({ [e.target.useremail]: e.target.value });
  };

  createUser = e => {
    e.preventDefault();
    axios.post(API_URL, this.state).then(() => {
      this.props.resetState();
      this.props.toggle();
    });
  };

  /*editStudent = e => {
    e.preventDefault();
    axios.put(API_URL + this.state.pk, this.state).then(() => {
      this.props.resetState();
      this.props.toggle();
    });
  };*/

  defaultIfEmpty = value => {
    return value === "" ? "" : value;
  };

  render() {
    return (
      
      /* <Form onSubmit={this.props.user ? this.editStudent : this.createStudent}>  */
      <Form onSubmit={this.createUser}>
        <FormGroup>
          <Label for="useremail">Correo electr칩nico:</Label>
          <Input
            type="text"
            name="useremail"
            onChange={this.onChange}
            value={this.defaultIfEmpty(this.state.useremail)}
            defaultValue="correo"
          />
        </FormGroup>
        <FormGroup>
          <Label for="userpassword">Contrase침a:</Label>
          <Input
            type="text"
            name="userpassword"
            onChange={this.onChange}
            value={this.defaultIfEmpty(this.state.userpassword)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="savedate">Fecha de creaci칩n:</Label>
          <Input
            type="date"
            name="savedate"
            onChange={this.onChange}
            value={this.defaultIfEmpty(this.state.savedate)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="changepassworddate">Fecha cambio contrase침a:</Label>
          <Input
            type="date"
            name="changepassworddate"
            onChange={this.onChange}
            value={this.defaultIfEmpty(this.state.changepassworddate)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="userroleid">Rol:</Label>
          <Input
            type="text"
            name="userroleid"
            onChange={this.onChange}
            value={this.defaultIfEmpty(this.state.userroleid)}
          />
        </FormGroup>
        <Button>Send</Button>
      </Form>
    );
  }
}

export default NewUserForm;
